import React, { Component, useState } from 'react';

// import CustomAlerts from './components/customAlerts';
import TableRow from '../tableRow';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserAlt, faUserAltSlash } from '@fortawesome/free-solid-svg-icons';
/**
 * @file module:src/components/table/index.jsx  
 * @param {number} props.index - Is the index of the line in the table
 * @returns the lines from the table, and in case of click 
 */


class CustomTable extends Component {
    constructor(props) {
        super(props);

        console.log("PROPS:", props);

    };

    getuserdatas() {
        let rows = this.props.tableCollumnsValues;
        let collumns = this.props.tableLine;
        
        return (
            rows.map((row, i) => {
                console.log("ROW:",row);
                return (
                    <tr>
                        {collumns.map((collumn, j) => {
                            console.log(collumn);
                            return (
                                <td>
                                    {row[collumn.collumn]}
                                </td>
                            )
                        })
                        }
                    </tr>
                )
            }
            )

        )
    };

    getHeader() {
        let headerTitles = this.props.tableLine;
        return (
            headerTitles.map((header, i) => {
                return (
                    <th>{headerTitles[i].headerName}</th>
                )
            })
        )
    };


    render() {
        return (
            <table style={{ width: "100%", align: "center" }}>
                <thead style={{ backgroundColor: "#ff7070" }}>
                    <tr>
                        {this.getHeader()}
                    </tr>
                </thead>
                <tbody>
                    {this.getuserdatas()}
                </tbody>
            </table>
        )
    }

};

export default CustomTable;
