import React, { useState } from 'react';

import './style.css';

export default function ModalCar({ carObject, closeHandler, saveChanges }) {

    const [carChanged, setChanges] = useState({
        carManufacturer: (carObject && carObject.car_manufacturer) ? carObject.car_manufacturer : "",
        carModel: (carObject && carObject.car_model) ? carObject.car_model : "",
        carName: (carObject && carObject.car_name) ? carObject.car_name : "",
        carFuel: (carObject && carObject.car_fuel) ? carObject.car_fuel : ""
    })
    console.log(carChanged);

    const handleChanges = (event) => {

        if (event.target.id === "brand")
            console.log("Mudanca marca");
        else if (event.target.id === "model")
            console.log("MODELO");
        else if (event.target.id === "name")
            console.log("NOME");
        else if (event.target.id === "fuel")
            console.log("COMBUSTIVEL");

        //    setChanges({})

        console.log(event.target);
    }

    console.log(carChanged)

    return (
        <div style={style.parentDiv}>
            <div style={style.modalDiv}>


                <div style={style.headerModal}>
                    <h2>Carro</h2>
                    <button style={style.modalButton} onClick={closeHandler}></button>
                </div>

                <form onSubmit={saveChanges}>
                    <div style={style.headerModalContent}>
                        <p style={style.headerModalLine}></p>
                        <span style={style.headerModalText}>Marca: </span>
                        <input  
                        type="text" 
                        style={style.headerInput} 
                        value={carChanged.carManufacturer} 
                        onChange={handleChanges}></input>
                    </div>

                    <div style={style.headerModalContent}>
                        <p style={style.headerModalLine}></p>
                        <span style={style.headerModalText}>Modelo: </span>
                        <input  
                        type="text" 
                        style={style.headerInput} 
                        value={carChanged.carModel} 
                        onChange={
                            event => setChanges(
                                currentState => ({ ...currentState, carModel: event.target.value })
                                )
                            } 
                            />
                            
                        </div>

                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>
                    <span style={style.headerModalText}>Nome: </span>
                    <input  
                    type="text" 
                    style={style.headerInput} 
                    value={carChanged.carName} 
                    onChange={
                        event => setChanges(
                            currentState => ({ ...currentState, carName: event.target.value })
                            )
                        } 
                        />
                </div>

                <div style={style.headerModalContent}>

                    <p style={style.headerModalLine}></p>
                    <span style={style.headerModalText}>Combust√≠vel: </span>
                    <input 
                    type="text" 
                    style={style.headerInput} 
                    value={carChanged.carFuel} 
                    onChange={
                        event => setChanges(
                            currentState => ({ ...currentState, carName: event.target.value })
                            )
                        } 
                        />

                </div>

                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>
                    <input type="submit" value="Salvar" style={style.headerButton}></input>
                </div>

                <p style={style.headerModalLine}></p>

                    </form>
        </div>
            </div >
    )
};

const style = {
    parentDiv: {
        backgroundColor: "rgb(163, 163, 163,0.8)",
        position: "fixed",
        zIndex: 10,
        width: "100%",
        height: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
    },

    modalDiv: {
        backgroundColor: "#fffafa",
        width: "50%",
        height: "50%",
        position: "relative"
    },

    modalButton: {
        fontSize: "15px",
        backgroundColor: "#ffc9c9",
        width: "30px",
        height: "30px",
        borderRadius: "50%",
        borderColor: "#ffff",
        position: "absolute",
        top: "-4px",
        right: "-4px",
        color: "#525252"
    },

    headerModal: {
        backgroundColor: "#ff8f8f",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
        height: "20%",
    },
    headerModalContent: {
        height: "20%",
        backgroundColor: "#ffffff"
    },
    headerModalText: {
        display: "inlineBlock",
        fontSize: "18px",
        padding: "10px 2%",
        justifyContent: "left",
        fontFamily: "Arial"
    },

    headerInput: {
        display: "inline",
        color: "black",
        width: "35%",
        height: "10px"
    },

    headerModalLine: {
        backgroundColor: "pink",
        width: "100%",
        height: "4px",
        marginTop: "0px"
    },

    headerButton: {
        display: "inline",
        height: "50px",
        width: "100px",
        backgroundColor: "#ff7d7d",
        color: "#ffffff",
        border: "none"
    }

};
