import React from 'react';

/**
 * @file module:src/components/customExpandedBox
 * @param {*} props
 * @returns 
 */


export default function CustomExpandedBox(props){
    return (
        <div style={props && props.boxHeigt ? {...style.boxWrapper,height:props.boxHeigt} : style.boxWrapper}>
            <h1 style={style.extendedTitle}>{props && props.boxTitle ? props.boxTitle : ""}</h1>
            {props }
            <p style={style.boxText}>{props && props.boxText ? props.boxText : "Sem texto"}</p>
        </div>
    )
}

const style = {
    boxWrapper: {
        backgroundColor: "black",
        width: "100%",
        height: "30%"
        
    },

    extendedTitle:{
        display: "flex",
        textAlign: "center",
        justifyContent: "center", 
        fontSize : "18px",
        color: "white",
        padding: "10px 2%"
    },

    boxText:{
        color: "#ffffff",
        padding: "20px 2%"
    }
}
