import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowsAltV, faCoffee } from '@fortawesome/free-solid-svg-icons'; 

const arrowIcon = <FontAwesomeIcon icon={faArrowsAltV} style={{fontSize:"30px",color:"#363636"}}/>



/**
 * @file module:src/components/customExpandedBox
 * @param {*} props
 * @returns 
 */

export default function CustomExpandedBox(props){
    console.log("field received",props.fieldValues);
    console.log("FIELD LIST",props.fieldsList);

    /**
     * Function to mount the fieldList 
     * @param
     * @returns
     */
    function mountFieldList(){
        let fieldValues = props.fieldValues;
        console.log("FIELD VALUES::",fieldValues);
        
        return (
        props.fieldsList.map((field) =>
            { 
            return ( 
                <div style={style.boxContent}>
                    <p style={style.boxText}> <strong>{field.label}:</strong> {fieldValues[field.fieldKey]}</p>
                </div>
            )}
        ) )
    };

    return (
        <div style={props && props.boxHeigt ? {...style.boxWrapper,height:props.boxHeigt} : style.boxWrapper}>
            <h1 style={style.extendedTitle}>{props && props.boxTitle ? props.boxTitle : ""}</h1>
            <button onClick={()=>alert("oi")} style={style.boxButton}>{arrowIcon}</button>
            {mountFieldList()} 
        </div>
    )
} 

const style = {
    boxWrapper: {
        backgroundColor: "#d9d9d9",
        width: "100%", 
        height: "30%",
        position: "relative"
    },
    extendedTitle:{
        display: "flex",
        textAlign: "center",
        justifyContent: "center", 
        fontSize : "18px",
        color: "#545454",
        padding: "16px 0"
    },
    boxText:{
        color: "#ffffff",
        padding: "20px 2%"
    },
    boxContent: {
        backgroundColor:"#fff",
        width:"100%",
        fontSize: "18px"
    },
    boxButton:{
        border: "none",
        textDecoration: "none",
        cursor: "pointer",
        width: "80px",
        height: "40px",
        borderRadius: "50%",
        position: "absolute",
        top: "10px",
        marginLeft: "2%"
        
    }
}
