import React, { useEffect, useState } from 'react';
import CustomCloseButton from '../customCloseButton';


export default function CustomAlerts(props){
    const [fade, setFadeEffect] = useState(
        false
    )

    useEffect
    
    return (
        
        <div style= { fade ? style.mountedAlert : style.unmountedAlert} >
            <div style={{position: "relative"}}>
                <p style={{
                    fontSize: "20px",
                    marginLeft:"5%"
                }} >{props && props.textAlert ? props.textAlert : "no message!"}</p>

            
                <CustomCloseButton
                    bgColor= { props && props.successAlert ? "#75eb95" :  "#fc8d8d"}
                />
            </div>     
        </div>
        


    )
} ;

const style = {
    unmountedAlert : {
        position:"absolute",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#6ecc7e",
        top:"-250px",
        left:"-250px",
        transition: "all 0.5s"
        // marginLeft: "1%"

    },

    mountedAlert: {
        position:"absolute",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#6ecc7e",
        top:"10px",
        left:"10px",
        transition: "all 0.5s"
    }, 

    failAlert: {
        position:"absolute",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#e32727"
    },

    fadeOut: {
        position:"fixed",
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        marginLeft: "1%",
        opacity: "1",
        backgroundColor: "white"
    }
}
