import React from 'react';
import CustomCloseButton from '../customCloseButton';

export default function CustomAlerts(props){
    return (
        <div style={ props && props.successAlert ? style.successAlert : style.failAlert} >
            <div style={{position: "relative"}}>
                <p style={{
                    fontSize: "20px",
                    marginLeft:"5%"
                }} >{props && props.textAlert ? props.textAlert : "no message!"}</p>

            
                <CustomCloseButton
                    bgColor= { props && props.successAlert ? "#75eb95" :  "#fc8d8d"}
                />
            </div>     
        </div>

    )
} ;

const style = {
    successAlert : {
        position:"fixed",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#6ecc7e",

    },

    failAlert: {
        position:"fixed",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#e32727"
    }

}
