import React from 'react';

export default function CustomAlerts(props){
    return (
        <div style={{
            position:"fixed",
            width: "45%",
            height:"100px", 
            backgroundColor: props && props.bgColor ? props.bgColor : "#42ff45" ,
            display: "flex",
            alignContent:"center",
            alignItems:"center"
        }}>
            <div style={{position: "relative"}}>
            <p style={{
                fontSize: "20px",
                marginLeft:"5%"
            }} >{props && props.textAlert ? props.textAlert : "no message!"}</p>

            
                <button style={{
                    fontSize: "15px",
                    backgroundColor: "#c8fac9",
                    width: "30px",
                    height: "30px",
                    borderRadius: "50%",
                    borderColor: "#ffff",
                    position: "absolute",
                    top: "-4px",
                    right: "-4px",
                    color: "#525252"
                }}>X</button>
            </div>     
        </div>

    )
} ;
