import React from 'react';

export default function CustomAlerts(props){
    return (
        <div style={{
            position:"fixed",
            width: "45%",
            height:"100px", 
            backgroundColor: props && props.bgColor ? props.bgColor : "#42ff45" ,
            display: "flex",
            alignContent:"center",
            alignItems:"center"
        }}>
           <p style={{
            fontSize: "20px",
            margin
        }} >{props && props.textAlert ? props.textAlert : "fail"}</p>
             
        </div>

    )
} ;
