import React, { useEffect, useState } from 'react';

import CustomInput from '../CustomInput/index';


export default function ModalCar({ carObject, closeHandler, saveChanges }) {

    const [carChanged, setChanges] = useState({
        carManufacturer: (carObject && carObject.car_manufacturer) ? carObject.car_manufacturer : "",
        carModel: (carObject && carObject.car_model) ? carObject.car_model : "",
        carName: (carObject && carObject.car_name) ? carObject.car_name : "",
        carFuel: (carObject && carObject.car_fuel) ? carObject.car_fuel : ""
    })

    const [readOnly, setReadOnlyState] = useState(
        true
    )

    function edit() {
        setReadOnlyState(!readOnly)
        console.log(readOnly)
    }

    console.log("Carro aberto", carChanged);

    const closeListener = (e) =>{
        // console.log(e)
        if (e.keyCode === 27)
            closeHandler();
    }
    useEffect(() => {
        window.addEventListener("keydown",closeListener); 

        return () => {
            console.log('Fechar modal! ');
            window.removeEventListener("keydown",closeListener); 

        }
    }, []);




    return (
        <div style={style.parentDiv} onClick={closeHandler}>
            <div style={style.modalDiv} onClick={event => event.stopPropagation()}>


                <div style={style.headerModal}>
                    <h1 style={{ color: "#ffffff" }}>Carro</h1>
                    <button style={style.modalButton} onClick={closeHandler}></button>
                </div>


                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>
                    <CustomInput
                        labelName="Marca "
                        type="text"
                        value={carChanged.carManufacturer}
                        onChange={
                            event => setChanges(
                                currentState => ({ ...currentState, carManufacturer: event.target.value })
                            )
                        }
                        readOnly={readOnly}

                    />

                </div>

                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>

                    <CustomInput
                        labelName="Modelo "
                        type="text"
                        value={carChanged.carModel}
                        onChange={
                            event => setChanges(
                                currentState => ({ ...currentState, carModel: event.target.value })
                            )
                        }
                        readOnly={readOnly}

                    />


                </div>

                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>

                    <CustomInput
                        labelName="Nome "
                        type="text"
                        value={carChanged.carName}
                        onChange={
                            event => setChanges(
                                currentState => ({ ...currentState, carName: event.target.value })
                            )
                        }
                        readOnly={readOnly}
                    />
                </div>

                <div style={style.headerModalContent}>

                    <p style={style.headerModalLine}></p>
                    <CustomInput
                        labelName="Combustível "
                        type="text"
                        value={carChanged.carFuel}
                        onChange={
                            event => setChanges(
                                currentState => ({ ...currentState, carFuel: event.target.value })
                            )
                        }
                        readOnly={readOnly}
                    />


                </div>

                <div style={style.headerModalContent}>
                    <p style={style.headerModalLine}></p>
                    {!readOnly ? <input type="submit" value="Salvar" onClick={() => saveChanges(carChanged, carObject)} 
                        style={{ ...style.headerButton, backgroundColor: "#ff8585" }} /> : ""}

                    <button onClick={() => edit()} 
                        style={readOnly ? style.headerDefault : style.headerButton}>
                        {readOnly ? "Editar" : "Cancelar"}
                    </button>

                </div>


                <p style={style.headerModalLine}></p>
            </div>
        </div >
    )
};

const style = {
    parentDiv: {
        backgroundColor: "rgb(163, 163, 163,0.8)",
        position: "fixed",
        zIndex: 10,
        width: "100%",
        height: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
    },

    modalDiv: {
        backgroundColor: "#fffafa",
        width: "50%",
        height: "50%",
        position: "relative"
    },

    modalButton: {
        fontSize: "15px",
        backgroundColor: "#ffc9c9",
        width: "30px",
        height: "30px",
        borderRadius: "50%",
        borderColor: "#ffff",
        position: "absolute",
        top: "-4px",
        right: "-4px",
        color: "#525252"
    },

    headerModal: {
        backgroundColor: "#ff8f8f",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
        height: "20%",
    },
    headerModalContent: {
        height: "20%",
        backgroundColor: "#ffffff"
    },

    headerModalLine: {
        backgroundColor: "pink",
        width: "100%",
        height: "4px",
        marginTop: "0px"
    },

    headerButton: {
        fontSize: "20px",
        display: "inline",
        height: "50px",
        width: "40%",
        margin: "0 5%",
        backgroundColor: "#ff7d7d",
        color: "#ffffff",
        border: "none",
        cursor: "pointer"
    },

    headerDefault: {
        fontSize: "20px",
        display: "inline",
        height: "50px",
        width: "80%",
        margin: "0 10%",
        backgroundColor: "#ff7d7d",
        color: "#ffffff",
        border: "none",
        cursor: "pointer"
    }
};
