import React from 'react';
import CustomButton from '../button';

export default function CustomAlerts(props){
    return (
        <div style={ style.failAlert } >
            <div style={{position: "relative"}}>
                <p style={{
                    fontSize: "20px",
                    marginLeft:"5%"
                }} >{props && props.textAlert ? props.textAlert : "no message!"}</p>

            
                <button style={{
                    fontSize: "15px",
                    backgroundColor: "#c8fac9",
                    width: "30px",
                    height: "30px",
                    borderRadius: "50%",
                    borderColor: "#ffff",
                    position: "absolute",
                    top: "0px",
                    right: "-12px",
                    color: "#525252"
                }}></button>
            </div>     
        </div>

    )
} ;

const style = {
    successAlert : {
        position:"fixed",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#6ecc7e",

    },
    failAlert: {
        position:"fixed",
        width: "300px",
        height:"100px", 
        display: "flex",
        alignContent:"center",
        alignItems:"center",
        backgroundColor: "#e32727"
    }

}
