import React,{ useEffect, useState }  from 'react';

import ExtendedTableRow from '../extendedTableRow';

export default function ExtendedLine(props){

    const [extend, setExtendTable] = useState(
        false
    ); 

    

    function extendtable() {
        console.log("Antes",extend);
        
        setExtendTable(!extend);

    }

    return (  
        <>
        <tr onClick={()=>extendtable()}  style={{backgroundColor: props.index % 2 === 0 ?   "#cfcfcf" :  "#fafffb",fontSize:"12px" }}> 
           <td> {props.children} </td>
        </tr>   
        {extend ? <ExtendedTableRow></ExtendedTableRow> : ""} 
        </>
        // extend ? <ExtendedTableRow> </ExtendedTableRow> : null 
    )
};
