import React,{ useEffect, useState, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowUp,faArrowRight} from '@fortawesome/free-solid-svg-icons'; 

const arrowUp = <FontAwesomeIcon icon={faArrowUp} style={{fontSize:"30px",color:"#363636"}}/>
const arrowRight = <FontAwesomeIcon icon={faArrowRight} style={{fontSize:"30px",color:"#363636"}}/>

/**
 * @file module:src/components/customExpandedBox
 * @param {*} props
 * @returns 
 */

export default function CustomExpandedBox(props){
    console.log("field received",props.fieldValues);
    console.log("FIELD LIST",props.fieldsList);

    const [boxColapsed, setBoxColapsed] = useState(false);
    

    function handleClick() {
        
        setBoxColapsed(!boxColapsed);
    }

    /**
     * Function to mount the fieldList 
     * @param
     * @returns
     */
    function mountFieldList(){
        let fieldValues = props.fieldValues;
        console.log("FIELD VALUES::",fieldValues);
        
        return (
        props.fieldsList.map((field, i) =>
            { 
            return ( 
                <div key={""+i} style={style.boxContent}>
                    <p style={style.boxText}>
                        <strong>{field.label}: </strong>
                        {fieldValues[field.fieldKey]}
                    </p>
                </div>
            )}
        ) )
    };

    return (
        <div style={props && props.boxHeigt ? {...style.boxWrapper,height:props.boxHeigt} : style.boxWrapper}>
            <h1 style={style.extendedTitle}>{props && props.boxTitle ? props.boxTitle : ""}</h1>
            <button onClick={()=>handleClick()} style={style.boxButton}>{boxColapsed ? arrowUp : arrowRight}</button>
            {boxColapsed ? mountFieldList() : null} 
        </div>
    )
} 

const style = {
    boxWrapper: {
        backgroundColor: "#d9d9d9",
        width: "100%", 
        height: "30%",
        position: "relative"
    },
    extendedTitle:{
        display: "flex",
        textAlign: "center",
        justifyContent: "center", 
        fontSize : "18px",
        color: "#545454",
        padding: "16px 0"
    },
    boxText:{
        margin: "0 0",
        padding: "5px 10px"
    },
    boxContent: {
        backgroundColor:"#f5f5f5",
        width:"100%",
        fontSize: "18px"
    },
    boxButton:{
        border: "none",
        textDecoration: "none",
        cursor: "pointer",
        width: "80px",
        height: "40px",
        borderRadius: "50%",
        position: "absolute",
        top: "10px",
        marginLeft: "2%"
        
    }
}
